import React from 'react' /*<------------------------------------------------Nos quedamos solo con React*/
	import PropTypes from 'prop-types'

	import ForecastItem from './ForecastItem'

	import './styles.css'

	const renderForecastItemDays = (forecastData) => { /*<----------------------Pasamos afuera como arrow function*/
	    return forecastData.map(forecast => 
		<ForecastItem 
		    key={`${forecast.weekDay}${forecast.hour}`}
		    weekDay={forecast.weekDay} 
		    hour={forecast.hour} 
		    data={forecast.data}
		/>)
	}

	const renderProgress = () => { /*<--------------------------------------------Pasamos afuera como arrow function*/
	    return 'Cargando pronóstico extendido...'
	}

	const ForecastExtended = ({ city, forecastData }) => ( /*<--------------------Creamos nuestro componente function qye trae como parámetros city y forecastData*/
		<div>
		    <h2 className='forecast-title'>Pronóstico Extendido para {city}</h2>
		    {
		        forecastData ? 
		        renderForecastItemDays(forecastData):
		        renderProgress()    
		    }
		</div>
	)

	ForecastExtended.propTypes = {
	    city: PropTypes.string.isRequired,
	    forecastData: PropTypes.array.isRequired, /*<------------------------------Pasamos forecastData por los prop types*/
	}

	export default ForecastExtended
