import React, { Component } from 'react'
import PropTypes from 'prop-types'

import ForecastItem from './ForecastItem'
import transformForecast from '../services/transformForecast'

import './styles.css'

/*const days = [
    'Lunes',
    'Martes',
    'Miércoles',
    'Jueves',
    'Viernes'
]

const data = {
    temperature: 10,
    weatherState: 'normal', 
    humidity: 10,
    wind: 'normal'
}*/

export const api_key = 'b47e17be31b6311bf670d92b8f350694'
export const url = 'http://api.openweathermap.org/data/2.5/forecast'

class ForecastExtended extends Component {

    constructor(){
        super()
        this.state = {
            forecastData: null
        }
    }

    componentDidMount(){
        const url_forecast = `${url}?q=${this.props.city}&appid=${api_key}`

        fetch(url_forecast).then(
            data => (data.json())
        ).then(
            weather_data => {
                console.log('weather_data',weather_data)
                const forecastData = transformForecast(weather_data)
                console.log('forecastData',forecastData)
                this.setState({ forecastData })
            }
        )
    }

    renderForecastItemDays(forecastData){
        return forecastData.map(forecast => 
            <ForecastItem 
                key={`${forecast.weekDay}${forecast.data}`}
                weekDay={forecast.weekDay} 
                hour={10} 
                data={forecast.data}
            />)
    }

    renderProgress = () => {
        return 'Cargando pronóstico extendido...'
    }

    render(){
        const { city }= this.props
        const { forecastData } = this.state
        return(
            <div>
                <h2 className='forecast-title'>Pronóstico Extendido para {city}</h2>
                {
                    forecastData ? 
                    this.renderForecastItemDays(forecastData):
                    this.renderProgress()    
                }
            </div>
        )
    }
}

ForecastExtended.propTypes = {
    city: PropTypes.string.isRequired,
}

export default ForecastExtended
